package exercises

import (
	"strconv"

	"github.com/nottheeconomist/adventofcode_2015/puzzle"
)

func getLevel(s string) int {
	i := 0
	for _, ch := range s {
		if ch == '(' {
			i++
		} else {
			i--
		}
	}
	return i
}

func getFirstBasementLevel(s string) int {
	i := 0
	for idx, ch := range s {
		if ch == '(' {
			i++
		} else {
			i--
		}
		if i == -1 {
			return idx + 1
		}
	}
	return 0
}

// DayOne runs the calculations for the Day One puzzle
func DayOne(p *puzzle.Puzzle) {
	p.AddSolution(strconv.Itoa(getLevel(dayoneinput)))
	p.AddSolution(strconv.Itoa(getFirstBasementLevel(dayoneinput)))
	
}

